{
  "name": "createSubscription",
  "description": "Workflow para capturar dados de faturamento para criação de uma assinatura Fieldlink",
  "blueprint_spec": {
    "requirements": [
      "core"
    ],
    "prepare": [],
    "nodes": [
      {
        "id": "START-CREATE-SUBSCRIPTION",
        "name": "Iniciar processo createSubscription",
        "next": "CONFIG",
        "type": "Start",
        "lane_id": "mayManageConfigurations",
        "parameters": {
          "input_schema": {
            "type": "object",
            "properties": {
              "origin": {
                "type": "string"
              }
            }
          },
          "timeout": 3600
        },
        "nodeSpec": "startCreateSubscription"
      },
      {
        "id": "CONFIG",
        "name": "guardar parâmetros do processo",
        "next": "GET-ACCOUNT",
        "type": "SystemTask",
        "lane_id": "mayManageConfigurations",
        "category": "setToBag",
        "parameters": {
          "input": {
            "is_authorized": {
              "$ref": "actor_data"
            },
            "actor_id": {
              "$ref": "actor_data.actor_id"
            },
            "account_id": {
              "$ref": "actor_data.account_id"
            },
            "event_id": {
              "$ref": "actor_data.event_id"
            },
            "origin": "/app",
            "email_suporte": "gustavo.haramura@fdte.io",
            "cliente_template": "d-19ac369b15dc42e49fc46dfba9e55eb0",
            "suporte_template": "d-ab16750033e74aeb9a2028787e2c67ae"
          }
        },
        "nodeSpec": "config"
      },
      {
        "id": "GET-ACCOUNT",
        "name": "consultar account",
        "next": "ACCOUNT-STATUS",
        "type": "SystemTask",
        "category": "getAccount",
        "lane_id": "mayManageConfigurations",
        "parameters": {
          "input": {
            "account_id": {
              "$ref": "bag.account_id"
            }
          }
        },
        "nodeSpec": "getAccount"
      },
      {
        "id": "ACCOUNT-STATUS",
        "name": "direcionar fluxo conforme aprovação do usuário",
        "next": {
          "active": "BAG-ACCOUNT",
          "default": "ALERT-SUBSCRIPTION-WARNING",
          "trial": "BAG-ACCOUNT",
          "trial_expired": "BAG-ACCOUNT"
        },
        "type": "Flow",
        "lane_id": "mayManageConfigurations",
        "parameters": {
          "input": {
            "decision": {
              "$ref": "result.accounts[0].status"
            }
          }
        },
        "nodeSpec": "checkAccountStatus"
      },
      {
        "id": "ALERT-SUBSCRIPTION-WARNING",
        "name": "Notificar usuário",
        "next": "FINISH-INVALID-ACCOUNT",
        "type": "UserTask",
        "lane_id": "actorId",
        "parameters": {
          "input": {
            "icon": "warning",
            "text": {
              "$mustache": "status: {{result.accounts[0].status}}"
            },
            "title": "Criar Assinatura"
          },
          "action": "SHOW_ALERT",
          "timeout": 60,
          "activity_schema": {
            "type": "object"
          }
        },
        "example": {
          "file": "showAlertInput",
          "schema": "showAlertInput"
        },
        "nodeSpec": "showAlert"
      },
      {
        "id": "FINISH-INVALID-ACCOUNT",
        "name": "finalizar processo createSubscription com erro - Status da conta indevido",
        "next": null,
        "type": "Finish",
        "lane_id": "actorId",
        "parameters": {
          "input": {
            "status": "INVALID ACCOUNT STATUS"
          }
        },
        "nodeSpec": "finishError"
      },
      {
        "id": "BAG-ACCOUNT",
        "name": "guardar account",
        "next": "GET-SIGNATURE-DATA",
        "type": "SystemTask",
        "lane_id": "mayManageConfigurations",
        "category": "setToBag",
        "parameters": {
          "input": {
            "account": {
              "$ref": "result.accounts[0]"
            }
          }
        },
        "nodeSpec": "bagAccount"
      },
      {
        "id": "GET-SIGNATURE-DATA",
        "name": "Obter dados da assinatura",
        "next": "BAG-BILLING",
        "type": "UserTask",
        "lane_id": "actorId",
        "parameters": {
          "input": {},
          "action": "GET_SIGNATURE_DATA",
          "timeout": 60
        },
        "nodeSpec": "getSignatureData"
      },
      {
        "id": "BAG-BILLING",
        "name": "Guardar dados de faturamento",
        "next": "SAVE-STATUS",
        "type": "SystemTask",
        "lane_id": "actorId",
        "category": "setToBag",
        "parameters": {
          "input": {
            "billing": {
              "$ref": "result.activities[0].data"
            }
          }
        },
        "nodeSpec": "bagBilling"
      },
      {
        "id": "SAVE-STATUS",
        "name": "Atualiza status da account",
        "next": "POST-SUPPORT-EMAIL",
        "type": "SystemTask",
        "lane_id": "actorId",
        "category": "saveStatus",
        "parameters": {
          "input": {
            "status": "active",
            "account_id": {
              "$ref": "bag.account_id"
            }
          }
        },
        "nodeSpec": "saveStatus"
      },
      {
        "id": "POST-SUPPORT-EMAIL",
        "name": "Enviar e-mail para suporte FL",
        "next": "CHECK-SUPPORT-HTTP-RESPONSE",
        "type": "SystemTask",
        "lane_id": "actorId",
        "category": "HTTP",
        "parameters": {
          "input": {
            "from": {
              "email": {
                "$ref": "environment.EMAIL_SENDER"
              }
            },
            "template_id": {
              "$ref": "bag.suporte_template"
            },
            "personalizations": [
              {
                "to": [
                  {
                    "email": {
                      "$ref": "bag.email_suporte"
                    }
                  }
                ],
                "dynamic_template_data": {
                  "cnpj": {
                    "$ref": "bag.billing.company_document"
                  },
                  "phone": {
                    "$ref": "bag.billing.company_phone_number"
                  },
                  "processId": {
                    "$ref": "bag.billing.processId"
                  },
                  "companyName": {
                    "$ref": "bag.billing.company_name"
                  },
                  "contactName": {
                    "$ref": "bag.billing.billing_contact_name"
                  },
                  "contactRole": {
                    "$ref": "bag.billing.company_phone_number"
                  },
                  "contactEmail": {
                    "$ref": "bag.billing.billing_contact_email"
                  },
                  "contactPhone": {
                    "$ref": "bag.billing.billing_contact_phone"
                  }
                }
              }
            ]
          },
          "request": {
            "url": "https://api.sendgrid.com/v3/mail/send",
            "verb": "POST",
            "headers": {
              "Authorization": {
                "$mustache": "Bearer {{&environment.SENDGRID_API_KEY}}",
                "Content-Type": "application/json"
              }
            }
          }
        },
        "nodeSpec": "postSupportEmail"
      },
      {
        "id": "CHECK-SUPPORT-HTTP-RESPONSE",
        "name": "checar código de resposta HTTP",
        "next": {
          "200": "POST-CLIENT-EMAIL",
          "201": "POST-CLIENT-EMAIL",
          "202": "POST-CLIENT-EMAIL",
          "204": "POST-CLIENT-EMAIL",
          "default": "ALERT-SEND-EMAIL-ERROR"
        },
        "type": "Flow",
        "lane_id": "actorId",
        "parameters": {
          "input": {
            "decision_key": {
              "$ref": "result.status"
            }
          }
        },
        "nodeSpec": "checkHTTPResponse"
      },
      {
        "id": "ALERT-SEND-EMAIL-ERROR",
        "name": "Notificar usuário",
        "next": "FINISH-CREATE-SUBSCRIPTION-SEND-EMAIL-ERROR",
        "type": "UserTask",
        "lane_id": "actorId",
        "parameters": {
          "input": {
            "icon": "error",
            "text": "Falha ao enviar e-mail."
          },
          "action": "SHOW_ALERT",
          "timeout": 100,
          "activity_schema": {
            "type": "object",
            "properties": {}
          }
        },
        "example": {
          "file": "showAlertInput",
          "schema": "showAlertInput"
        },
        "nodeSpec": "showAlert"
      },
      {
        "id": "FINISH-CREATE-SUBSCRIPTION-SEND-EMAIL-ERROR",
        "name": "finalizar o processo createSubscription com erro - e-mail não enviado",
        "next": null,
        "type": "Finish",
        "lane_id": "actorId",
        "parameters": {
          "input": {
            "status": "SENDING EMAIL ERROR"
          }
        },
        "nodeSpec": "finishError"
      },
      {
        "id": "POST-CLIENT-EMAIL",
        "name": "Enviar e-mail para o cliente",
        "next": "CHECK-CLIENT-HTTP-RESPONSE",
        "type": "SystemTask",
        "lane_id": "actorId",
        "category": "HTTP",
        "parameters": {
          "input": {
            "from": {
              "email": {
                "$ref": "bag.billing.billing_contact_email"
              }
            },
            "template_id": {
              "$ref": "bag.cliente_template"
            },
            "personalizations": [
              {
                "to": [
                  {
                    "email": {
                      "$ref": "bag.email_suporte"
                    }
                  }
                ],
                "dynamic_template_data": {
                  "cnpj": {
                    "$ref": "bag.billing.company_document"
                  },
                  "phone": {
                    "$ref": "bag.billing.company_phone_number"
                  },
                  "processId": {
                    "$ref": "bag.billing.processId"
                  },
                  "companyName": {
                    "$ref": "bag.billing.company_name"
                  },
                  "contactName": {
                    "$ref": "bag.billing.billing_contact_name"
                  },
                  "contactRole": {
                    "$ref": "bag.billing.company_phone_number"
                  },
                  "contactEmail": {
                    "$ref": "bag.billing.billing_contact_email"
                  },
                  "contactPhone": {
                    "$ref": "bag.billing.billing_contact_phone"
                  }
                }
              }
            ]
          },
          "request": {
            "url": "https://api.sendgrid.com/v3/mail/send",
            "verb": "POST",
            "headers": {
              "Authorization": {
                "$mustache": "Bearer {{&environment.SENDGRID_API_KEY}}",
                "Content-Type": "application/json"
              }
            }
          }
        },
        "nodeSpec": "postClientEmail"
      },
      {
        "id": "CHECK-CLIENT-HTTP-RESPONSE",
        "name": "checar código de resposta HTTP",
        "next": {
          "200": "ALERT-SUBSCRIPTION-SUCCESS",
          "201": "ALERT-SUBSCRIPTION-SUCCESS",
          "202": "ALERT-SUBSCRIPTION-SUCCESS",
          "204": "ALERT-SUBSCRIPTION-SUCCESS",
          "default": "ALERT-SEND-EMAIL-ERROR"
        },
        "type": "Flow",
        "lane_id": "actorId",
        "parameters": {
          "input": {
            "decision_key": {
              "$ref": "result.status"
            }
          }
        },
        "nodeSpec": "checkHTTPResponse"
      },
      {
        "id": "ALERT-SUBSCRIPTION-SUCCESS",
        "name": "Notificar usuário",
        "next": "REDIRECT-TO-SIGNATURE",
        "type": "UserTask",
        "lane_id": "actorId",
        "parameters": {
          "input": {
            "icon": "success",
            "text": "Seus dados de faturamento foram recebidos, em breve nosso suporte entrará em contato.",
            "title": "Nova assinatura"
          },
          "action": "SHOW_ALERT",
          "timeout": 10,
          "activity_schema": {
            "type": "object"
          }
        },
        "example": {
          "file": "showAlertInput",
          "schema": "showAlertInput"
        },
        "nodeSpec": "showAlert"
      },
      {
        "id": "REDIRECT-TO-SIGNATURE",
        "name": "Mostrar formulário personalizado",
        "next": "FINISH-CREATE-SUBSCRIPTION-SUCCESS",
        "type": "UserTask",
        "lane_id": "actorId",
        "parameters": {
          "input": {
            "route": {
              "$ref": "bag.origin"
            }
          },
          "action": "REDIRECT_TO",
          "timeout": 60,
          "activity_schema": {
            "type": "object"
          }
        },
        "example": {
          "file": "redirectToInput",
          "schema": "redirectToInput"
        },
        "nodeSpec": "redirectTo"
      },
      {
        "id": "FINISH-CREATE-SUBSCRIPTION-SUCCESS",
        "name": "finalizar processo createSubscription com sucesso",
        "next": null,
        "type": "Finish",
        "lane_id": "actorId",
        "nodeSpec": "finishSuccess"
      }
    ],
    "lanes": [
      {
        "id": "mayManageConfigurations",
        "name": "checks if actor data has claim may_manage_configurations",
        "rule": [
          "fn",
          [
            "actor_data",
            "bag"
          ],
          [
            "eval",
            [
              "apply",
              "or",
              [
                "map",
                [
                  "fn",
                  [
                    "v"
                  ],
                  [
                    "=",
                    "v",
                    [
                      "`",
                      "may_manage_configurations"
                    ]
                  ]
                ],
                [
                  "get",
                  "actor_data",
                  [
                    "`",
                    "claims"
                  ]
                ]
              ]
            ]
          ]
        ]
      },
      {
        "id": "actorId",
        "name": "restricted",
        "rule": [
          "fn",
          [
            "actor_data",
            "bag"
          ],
          [
            "=",
            [
              "get",
              [
                "get",
                "bag",
                [
                  "`",
                  "is_authorized"
                ]
              ],
              [
                "`",
                "actor_id"
              ]
            ],
            [
              "get",
              "actor_data",
              [
                "`",
                "actor_id"
              ]
            ]
          ]
        ]
      }
    ],
    "environment": {
    }
  }
}

{
  "name": "ATV_ADDRESS",
  "description": "ATV_ADDRESS",
  "blueprint_spec": {
    "lanes": [
      {
        "id": "1",
        "name": "creatorOnly",
        "rule": [
          "fn",
          [
            "actor_data",
            "bag"
          ],
          [
            "=",
            [
              "get",
              "bag",
              [
                "`",
                "creatorId"
              ]
            ],
            [
              "get",
              "actor_data",
              [
                "`",
                "actor_id"
              ]
            ]
          ]
        ]
      },
      {
        "id": "99",
        "name": "public",
        "rule": [
          "fn",
          [
            "&",
            "args"
          ],
          true
        ]
      }
    ],
    "nodes": [
      {
        "id": "1",
        "name": "Start node",
        "next": "2",
        "type": "Start",
        "lane_id": "99",
        "parameters": {
          "input_schema": {}
        }
      },
      {
        "id": "2",
        "name": "Set to bag node",
        "next": "7",
        "type": "SystemTask",
        "lane_id": "99",
        "category": "setToBag",
        "parameters": {
          "input": {
            "creatorId": {
              "$ref": "actor_data.actor_id"
            }
          }
        }
      },
      {
        "id": "3",
        "name": "Http node",
        "next": "4",
        "type": "SystemTask",
        "lane_id": "1",
        "category": "http",
        "parameters": {
          "input": {},
          "request": {
            "url": {
              "$mustache": "https://api.{{{environment.BASE_URL}}}/user/profile"
            },
            "verb": "GET",
            "headers": {
              "Authorization": {
                "$mustache": "UserLogin apikey=\"{{{actor_data.lendico_token}}}\""
              }
            }
          },
          "valid_response_codes": [
            200,
            204
          ]
        }
      },
      {
        "id": "4",
        "name": "Set to bag node",
        "next": "5",
        "type": "SystemTask",
        "lane_id": "1",
        "category": "setToBag",
        "parameters": {
          "input": {
            "profile": {
              "$ref": "result.data"
            }
          }
        }
      },
      {
        "id": "5",
        "name": "Http node",
        "next": "6",
        "type": "SystemTask",
        "lane_id": "1",
        "category": "http",
        "parameters": {
          "input": {},
          "request": {
            "url": {
              "$mustache": "https://lists.{{{environment.BASE_URL}}}/api/states?order_by=rank"
            },
            "verb": "GET"
          },
          "valid_response_codes": [
            200,
            204
          ]
        }
      },
      {
        "id": "6",
        "name": "Set to bag node",
        "next": "7",
        "type": "SystemTask",
        "lane_id": "1",
        "category": "setToBag",
        "parameters": {
          "input": {
            "states": {
              "$ref": "result.data.states"
            }
          }
        }
      },
      {
        "id": "7",
        "name": "User task node",
        "next": "8",
        "type": "UserTask",
        "lane_id": "1",
        "parameters": {
          "input": {
            "cpf": {
              "$ref": "bag.cpf"
            },
            "back": "voltar",
            "name": {
              "$ref": "bag.name"
            },
            "step": 3,
            "states": {
              "$ref": "bag.states"
            },
            "proceed": "prosseguir",
            "profile": {
              "$ref": "bag.profile"
            },
            "city_url": {
              "$mustache": "https://lists.{{{environment.BASE_URL}}}/api/states/:stateId/cities?order_by=rank&limit=1000"
            },
            "current_user": {
              "$ref": "bag.creatorId"
            },
            "best_proposal_url": {
              "$mustache": "https://api.{{{environment.BASE_URL}}}/approval/best_proposal"
            },
            "authorization_header": {
              "$mustache": "UserLogin apikey=\"{{{actor_data.lendico_token}}}\""
            }
          },
          "action": "ADDRESS_INFO",
          "activity_manager": "commit"
        }
      },
      {
        "id": "8",
        "name": "Flow node",
        "next": {
          "voltar": "20",
          "default": "7",
          "prosseguir": "10"
        },
        "type": "Flow",
        "lane_id": "1",
        "parameters": {
          "input": {
            "decision": {
              "$ref": "result.activities[0].data.action"
            }
          }
        }
      },
      {
        "id": "10",
        "name": "Http node",
        "next": "11",
        "type": "SystemTask",
        "lane_id": "1",
        "category": "http",
        "parameters": {
          "input": {
            "$ref": "result.activities[0].data.data"
          },
          "request": {
            "url": {
              "$mustache": "https://api.{{{environment.BASE_URL}}}/user/profile"
            },
            "verb": "POST",
            "headers": {
              "Authorization": {
                "$mustache": "UserLogin apikey=\"{{{actor_data.lendico_token}}}\""
              }
            }
          },
          "valid_response_codes": [
            200,
            204
          ]
        }
      },
      {
        "id": "11",
        "name": "Start process node",
        "next": "99",
        "type": "SystemTask",
        "lane_id": "1",
        "category": "startProcess",
        "parameters": {
          "input": {
            "cpf": {
              "$ref": "bag.cpf"
            },
            "name": {
              "$ref": "bag.name"
            }
          },
          "actor_data": {
            "$ref": "actor_data"
          },
          "workflow_name": "ATV_MOBILE_CONFIRMATION"
        }
      },
      {
        "id": "99",
        "name": "Finish node",
        "next": null,
        "type": "Finish",
        "lane_id": "1"
      },
      {
        "id": "20",
        "name": "Start process node",
        "next": "199",
        "type": "SystemTask",
        "lane_id": "1",
        "category": "startProcess",
        "parameters": {
          "input": {
            "cpf": {
              "$ref": "bag.cpf"
            },
            "name": {
              "$ref": "bag.name"
            }
          },
          "actor_data": {
            "$ref": "actor_data"
          },
          "workflow_name": "ATV_IDENTIFICATION_INFOS"
        }
      },
      {
        "id": "199",
        "name": "Finish node",
        "next": null,
        "type": "Finish",
        "lane_id": "1"
      }
    ],
    "prepare": [],
    "environment": {
      "ACQ_URL": "ACQ_URL",
      "ATV_URL": "ATV_URL",
      "RET_URL": "RET_URL",
      "BASE_URL": "BASE_URL",
      "PARTNERS_URL": "PARTNERS_URL"
    },
    "requirements": [
      "core"
    ]
  }
}
